import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ExerciseService } from './exercise.service';
@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent {

    exerciseDetails = null as any;
    exerciseToUpdate = {
        name:"",
        weigth:"",
        repetitions:"",
        machine:""
      }

    constructor(private exerciseService: ExerciseService) {
        this.getExercises();
    }
    closeExamplePopup() {
        const examplePopup = document.getElementById('examplePopup');
        if (examplePopup) {
            examplePopup.style.display = 'none';
        }
    }

    openExamplePopup() {
        const examplePopup = document.getElementById('examplePopup');
        if (examplePopup) {
            examplePopup.style.display = 'block';
        }
    }

    openPopupOnClick() {
        this.openExamplePopup();
    }

    createExercise(event: Event) {
        event.preventDefault();

        const exerciseData = {
            name: (document.getElementById('exerciseName') as HTMLInputElement).value,
            repetitions: parseInt((document.getElementById('exerciseRepetitions') as HTMLInputElement).value, 10),
            weight: parseFloat((document.getElementById('exerciseWeight') as HTMLInputElement).value),
            machine: (document.getElementById('exerciseMachine') as HTMLSelectElement).value === 'true'
        };
        this.exerciseService.postExercise(exerciseData).subscribe(
            (resp) => {
                console.log(resp);
                this.exerciseDetails = resp;
            },
            (err) => {
                console.log(err);
            }
        );
    }
    edit(studuent: any){
        this.studentToUpdate = studuent;
      }


    getExercises() {
        this.exerciseService.getExercises().subscribe(
            (resp) => {
                console.log(resp);
                this.exerciseDetails = resp;
            },
            (err) => {
                console.log(err);
            }
        );
    }

    deleteExercise(student: any) {
        this.exerciseService.deleteExercise(student.rollNumber).subscribe(
          (resp) => {
            console.log(resp);
            this.getStudentsDetails();
          },
          (err) => {
            console.log(err);
          }
        );
      }


      updateExercise(){
        this.exerciseService.updateExercise(this.studentToUpdate).subscribe(
          (resp) => {
            console.log(resp);
          },
          (err) => {
            console.log(err);
          }
        );
      }
}
