import { Component, Output, EventEmitter, Input } from '@angular/core';

@Component({
    selector: 'app-weekly-calendar',
    templateUrl: './weekly-calendar.component.html',
    styleUrls: ['./weekly-calendar.component.css'],
})
export class WeeklyCalendarComponent {
    // filteredExercises: any[] = [];
    filteredMondayExercises: any[] = []; // Declare a variable to hold filtered exercises for Monday

    @Input() exercises: any[] = []; // Ensure 'exercises' is of the appropriate type

    @Output() squareClicked: EventEmitter<any> = new EventEmitter();

    constructor() {
        // Initialize filteredMondayExercises with Monday's exercises on component initialization
        this.filteredMondayExercises = this.filterExercisesByDay("Monday");
    }

    openPopupOnClick() {
        this.squareClicked.emit();
    }

    filterExercisesByDay(day: string): any[] {
        console.log('Filtering for day:', day);
        console.log('ToDo::', this.exercises);
        const filteredExercises = this.exercises.filter((exercise: any) => exercise.day == day);
        console.log('Filtered exercises:', filteredExercises);
        return filteredExercises;
    }

}
