import { Component, Output, EventEmitter } from '@angular/core';

@Component({
    selector: 'app-weekly-calendar',
    templateUrl: './weekly-calendar.component.html',
    styleUrls: ['./weekly-calendar.component.css'],
})
export class WeeklyCalendarComponent {
    days: string[] = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

    @Output() squareClicked: EventEmitter<any> = new EventEmitter();

    openPopupOnClick() {
        this.squareClicked.emit();
    }
}
<div class="calendar-background">
    <div class="calendar-container">
        <h3>GYM PLANNING</h3>
        <!-- Displaying fetched exercises -->
        <div *ngIf="exerciseDetails">
            <div *ngFor="let exercise of exerciseDetails">
                <p>{{ exercise.name }}</p>
                <!-- Add more properties as needed -->
            </div>
        </div>

        <!-- Rest of your calendar structure -->
        <div id="calendar">
            <div class="placeholder"></div>
            <div class="calendar-events"></div>
        </div>
        <div class="day-title">
            <div class="day" *ngFor="let day of days">{{ day }}</div>
        </div>
        <div class="day-content">
            <div class="square" *ngFor="let day of days" (click)="openPopupOnClick()"></div>
        </div>
    </div>
</div>
