import { Component } from '@angular/core';
import { ExerciseService } from './exercise.service';

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.css']
})
export class AppComponent {
    selectedDay: string = '';

    exerciseDetails: any = null;
    exerciseToUpdate = {
        id: '',
        name: '',
        weight: '',
        repetitions: '',
        machine: ''
    };

    constructor(private exerciseService: ExerciseService) {
        this.getExercises();
    }

    closeExamplePopup(): void {
        const examplePopup = document.getElementById('examplePopup');
        if (examplePopup) {
            examplePopup.style.display = 'none';
        }
    }

    openExamplePopup(day: string): void {
        this.selectedDay = day;
        const examplePopup = document.getElementById('examplePopup');
        if (examplePopup) {
            examplePopup.style.display = 'block';
        }
    }

    createExercise(): void {
        console.log('event:', event);
        const exerciseData = {
            name: (document.getElementById('exerciseName') as HTMLInputElement).value,
            repetitions: parseInt((document.getElementById('exerciseRepetitions') as HTMLInputElement).value, 10),
            weight: parseFloat((document.getElementById('exerciseWeight') as HTMLInputElement).value),
            machine: (document.getElementById('exerciseMachine') as HTMLSelectElement).value === 'true',
            day: this.selectedDay
        };

        this.exerciseService.postExercise(exerciseData).subscribe(
            (resp) => {
                console.log('Exercise created:', resp);
                this.getExercises(); // Update the exercise list after a successful creation
                this.closeExamplePopup(); // Close the modal after creating the exercise
            },
            (err) => {
                console.error('Error creating exercise:', err);
            }
        );
    }

    getExercises(): void {
        this.exerciseService.getExercises().subscribe(
            (resp) => {
                console.log('Exercises:', resp);
                this.exerciseDetails = resp;
            },
            (err) => {
                console.error('Error fetching exercises:', err);
            }
        );
    }

    deleteExercise(exercise: any): void {
        this.exerciseService.deleteExercise(exercise.id).subscribe(
            () => {
                console.log('Exercise deleted');
                this.getExercises(); // Refresh the exercise list after deletion
            },
            (err) => {
                console.error('Error deleting exercise:', err);
            }
        );
    }

    edit(exercise: any): void {
        this.exerciseToUpdate = exercise;
    }

    updateExercise(): void {
        this.exerciseService.updateExercise(this.exerciseToUpdate.id, this.exerciseToUpdate).subscribe(
            (resp) => {
                console.log('Exercise updated:', resp);
            },
            (err) => {
                console.error('Error updating exercise:', err);
            }
        );
    }




}
<div class="content" role="main">
    <app-weekly-calendar (squareClicked)="openExamplePopup($event)" [exercises]="exerciseDetails"></app-weekly-calendar>
</div>

<div class="modal" tabindex="-1" role="dialog" id="examplePopup">
    <div class="modal-dialog" role="document">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title">Create Exercise</h5>
                <button type="button" class="close" aria-label="Close" (click)="closeExamplePopup()">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div class="modal-body">
                <!--ToDo:Change this-->
                <form (submit)="createExercise()">
                    <div class="form-group">
                        <label for="exerciseName">Name</label>
                        <input type="text" class="form-control" id="exerciseName" placeholder="Exercise Name">
                    </div>
                    <div class="form-group">
                        <label for="exerciseRepetitions">Repetitions</label>
                        <input type="number" class="form-control" id="exerciseRepetitions" placeholder="Repetitions">
                    </div>
                    <div class="form-group">
                        <label for="exerciseWeight">Weight</label>
                        <input type="number" class="form-control" id="exerciseWeight" placeholder="Weight">
                    </div>
                    <div class="form-group">
                        <label for="exerciseMachine">Machine</label>
                        <select class="form-control" id="exerciseMachine">
                            <option value="true">Yes</option>
                            <option value="false">No</option>
                        </select>
                    </div>
                    <button type="submit" class="btn btn-primary">Create</button>
                </form>
            </div>
        </div>
    </div>
</div>

<div class="modal" tabindex="-1" role="dialog" id="editExercisePopup">
    <div class="modal-dialog" role="document">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title">Edit Exercise</h5>
                <button type="button" class="close" aria-label="Close" (click)="closeEditExercisePopup()">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div class="modal-body">
                <!--ToDo:Change this-->
                <form (submit)="createExercise()">
                    <div class="form-group">
                        <label for="exerciseName">Name</label>
                        <input type="text" class="form-control" id="exerciseName" placeholder="Exercise Name">
                    </div>
                    <div class="form-group">
                        <label for="exerciseRepetitions">Repetitions</label>
                        <input type="number" class="form-control" id="exerciseRepetitions" placeholder="Repetitions">
                    </div>
                    <div class="form-group">
                        <label for="exerciseWeight">Weight</label>
                        <input type="number" class="form-control" id="exerciseWeight" placeholder="Weight">
                    </div>
                    <div class="form-group">
                        <label for="exerciseMachine">Machine</label>
                        <select class="form-control" id="exerciseMachine">
                            <option value="true">Yes</option>
                            <option value="false">No</option>
                        </select>
                    </div>
                    <button type="submit" class="btn btn-primary">Confirm</button>
                </form>
            </div>
        </div>
    </div>
</div>
<!-- Add this inside the modal to display the edit form -->
<div class="modal" tabindex="-1" role="dialog" id="editExercisePopup" *ngIf="showEditPopup">
    <div class="modal-dialog" role="document">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title">Edit Exercise</h5>
                <button type="button" class="close" aria-label="Close" (click)="closeEditPopup()">
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <div class="modal-body">
                <form (submit)="updateExercise()">
                    <div class="form-group">
                        <label for="editExerciseName">Name</label>
                        <input type="text" class="form-control" id="editExerciseName" [(ngModel)]="selectedExercise.name" placeholder="Exercise Name">
                    </div>
                    <div class="form-group">
                        <label for="editExerciseRepetitions">Repetitions</label>
                        <input type="number" class="form-control" id="editExerciseRepetitions" [(ngModel)]="selectedExercise.repetitions" placeholder="Repetitions">
                    </div>
                    <div class="form-group">
                        <label for="editExerciseWeight">Weight</label>
                        <input type="number" class="form-control" id="editExerciseWeight" [(ngModel)]="selectedExercise.weight" placeholder="Weight">
                    </div>
                    <div class="form-group">
                        <label for="editExerciseMachine">Machine</label>
                        <select class="form-control" id="editExerciseMachine" [(ngModel)]="selectedExercise.machine">
                            <option value="true">Yes</option>
                            <option value="false">No</option>
                        </select>
                    </div>
                    <button type="submit" class="btn btn-primary">Update</button>
                </form>
            </div>
        </div>
    </div>
</div>
