import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  exercise = {
    name: '',
    description: '',
    // Add other exercise properties
  };

  closeExerciseModal() {
    const exerciseModal = document.getElementById('exerciseModal');
    if (exerciseModal) {
      exerciseModal.style.display = 'none';
    }
  }

  createExercise() {
    // Logic to create an Exercise entity
    // Access this.exercise to get the entered Exercise data
    // Send this data to your backend service (e.g., via Angular HttpClient)
    // Close the modal after creating the Exercise
    this.closeExerciseModal();
  }
}
