import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  closeExamplePopup() {
    const examplePopup = document.getElementById('examplePopup');
    if (examplePopup) {
      examplePopup.style.display = 'none';
    }
  }

  openExamplePopup() {
    const examplePopup = document.getElementById('examplePopup');
    if (examplePopup) {
      examplePopup.style.display = 'block';
    }
  }

  openPopupOnClick() {
    // Here, you can put logic for specific actions related to the clicked square if needed
    this.openExamplePopup();
  }
}
