import { Component, Output, EventEmitter, Input } from '@angular/core';

@Component({
    selector: 'app-weekly-calendar',
    templateUrl: './weekly-calendar.component.html',
    styleUrls: ['./weekly-calendar.component.css'],
})
export class WeeklyCalendarComponent {
    days: string[] = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    @Input() exercises: any[] = []; // Ensure 'exercises' is of the appropriate type

    @Output() squareClicked: EventEmitter<any> = new EventEmitter();

    openPopupOnClick() {
        this.squareClicked.emit();
    }
}
<div class="calendar-background">
    <div class="calendar-container">
        <h3>GYM PLANNING</h3>
        <div id="calendar">
            <div class="placeholder"></div>
            <div class="calendar-events">
                <div class="day-title">
                    <div class="day" *ngFor="let day of days">{{ day }}</div>
                </div>

                <div class="day-content">
                    <div class="square" *ngFor="let day of days" (click)="openPopupOnClick()">
                        <!-- Content for Monday -->
                        <div *ngFor="let exercise of exercises" class="exercise">
                            <div *ngIf="exercise.reps === 12">
                            <div class="exercise-details">
                                    <div class="name">{{ exercise.name }}</div>
                                    <div class="repetitions">Reps: {{ exercise.repetitions }}</div>
                                    <div class="weight">Weight: {{ exercise.weight }}</div>
                                    <!-- Add other exercise details as needed -->
                                </div>
                            </div>
                        </div>
                    </div>
                    <!-- Add other days' content here if needed -->
                </div>
            </div>
        </div>
    </div>
</div>
